sourcesContent:
document.addEventListener('turbolinks:load', function() {
  const savedOrder = localStorage.getItem('note_order');
  
  if (savedOrder) {
    const selectOrder = document.querySelector('#orderSelect');
    
    // Comprobamos si el valor actual del select es diferente al valor guardado
    if (selectOrder.value !== savedOrder) {
      // Aplicamos la preferencia guardada al select
      selectOrder.value = savedOrder;

      // Enviamos el formulario con el nuevo valor (esto hará que el controller reciba el orden)
      document.querySelector('#orderForm').submit(); // Enviamos el formulario
    }
  }
  
  document.querySelector('#orderForm').addEventListener('submit', function(event) {
    const selectedOrder = document.querySelector('select[name="order"]').value;
    localStorage.setItem('note_order', selectedOrder); // Guardamos la preferencia en localStorage
  });
});
