document.addEventListener('turbolinks:load', function() {
  const savedOrder = localStorage.getItem('note_order');
  const selectOrder = document.querySelector('#orderSelect');

  //console.log(savedOrder)
  
  // if (savedOrder && savedOrder !== selectOrder.value) {
  //   // Si es diferente, asignamos el valor y enviamos el formulario
  //   selectOrder.value = savedOrder;
  //   // Enviar el formulario
  //   document.querySelector('#orderForm').submit();
  // }
  
  document.querySelector('#orderForm').addEventListener('submit', function(event) {
    const selectedOrder = document.querySelector('#orderSelect').value;
    localStorage.setItem('note_orders', selectedOrder); // Guardamos el orden seleccionado
  });
});
