document.addEventListener('turbolinks:load', function() {
  console.log("Hola")
  const savedOrder = localStorage.getItem('order');

  // Si hay un valor guardado, establecerlo en el select
  if (savedOrder) {
    const selectElement = document.getElementById('order_select');
    selectElement.value = savedOrder;
  }

  // Obtener el formulario y el botón de aplicar
  const form = document.getElementById('order_form');
  const applyButton = document.getElementById('apply_button');
  
  // Escuchar el evento de clic en el botón de aplicar
  applyButton.addEventListener('click', function(event) {
    event.preventDefault();  // Prevenir el comportamiento predeterminado del formulario

    const selectedOrder = document.getElementById('order_select').value;

    // Guardar el orden seleccionado en localStorage
    localStorage.setItem('order', selectedOrder);

    // Puedes hacer una solicitud para cargar las notas con el orden seleccionado si lo necesitas,
    // pero si solo estás usando el parámetro en la URL, no necesitas hacer nada más.
    
    // Enviar el formulario con el orden seleccionado
    form.submit();
  });
});
