document.addEventListener('turbolinks:load', function() {
  const themeToggles = document.querySelectorAll(".theme-toggle");

// Verifica si el modo oscuro está activado en el localStorage
const isDarkMode = localStorage.getItem("darkMode") === "true";

// Si el modo oscuro está activado, aplica la clase y actualiza los textos de los botones
if (isDarkMode) {
  document.body.classList.add("dark-mode");
  themeToggles.forEach((toggle) => {
    toggle.textContent = window.translations.light_mode;
  });
}

// Agrega un event listener a cada botón para manejar los clics
themeToggles.forEach((toggle) => {
  toggle.addEventListener("click", () => {
    // Alterna la clase "dark-mode" en el <body>
    const isDarkMode = document.body.classList.toggle("dark-mode");

    // Actualiza el texto de todos los botones según el estado actual
    themeToggles.forEach((btn) => {
      btn.textContent = isDarkMode
        ? window.translations.light_mode
        : window.translations.dark_mode;
    });

    // Guarda el estado actual en el localStorage
    localStorage.setItem("darkMode", isDarkMode);
  });
});
});
