document.addEventListener('turbolinks:load', function() {
  const savedOrder = localStorage.getItem('note_order');
  
  console.log(savedOrder)

  if (savedOrder) {
    document.querySelector('select[name="order"]').value = savedOrder;
  }
  
  document.querySelector('select[name="order"]').addEventListener('change', function() {
    localStorage.setItem('note_order', this.value);

    console.log("Cargye")

    const currentUrl = new URL(window.location.href);
    currentUrl.searchParams.set('order', this.value); 
    window.history.pushState({}, '', currentUrl);
  });
});
