document.addEventListener('turbolinks:load', function() {
  const orderSelect = document.querySelector('select[name="order"]');
  
  // Revisamos si ya hay un valor guardado en localStorage
  const savedOrder = localStorage.getItem('note_order');
  if (savedOrder) {
    orderSelect.value = savedOrder;
  }

  // Cuando el usuario cambia el orden
  orderSelect.addEventListener('change', function() {
    // Guardamos el valor en localStorage
    localStorage.setItem('note_order', this.value);

    // Actualizamos la URL para reflejar el cambio sin recargar la página
    const currentUrl = new URL(window.location.href);
    currentUrl.searchParams.set('order', this.value);  // Establece el parámetro `order`
    window.history.pushState({}, '', currentUrl);  // Actualiza la URL sin recargar la página

    // Opcionalmente, puedes hacer una solicitud al servidor para aplicar el nuevo orden
    // window.location.href = currentUrl.href;  // Descomenta esta línea si quieres recargar la página
  });
});
