document.addEventListener('turbolinks:load', function() {
  const savedOrder = localStorage.getItem('noteOrder');
  
  if (savedOrder) {
    // Si existe un valor, lo aplicamos al select
    document.querySelector('select[name="order"]').value = savedOrder;
    
    // Luego, enviamos el valor a través del formulario si es necesario
    document.querySelector('form').submit();
  }
  
  document.querySelector('form').addEventListener('submit', function(event) {
    const selectedOrder = document.querySelector('select[name="order"]').value;
    localStorage.setItem('noteOrder', selectedOrder); // Guardamos la preferencia en localStorage
  });
});
